package com.olivee.hrmanager.web.action.employee;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.ResponseBody;import com.olivee.hrmanager.web.entity.HrEmployee;import com.olivee.hrmanager.web.logic.UserManagerLogic;@Controller("employee.MainAction")public class MainAction {		@Autowired	private UserManagerLogic userManagerLogic;		@RequestMapping("/user/list")	@ResponseBody	public List<HrEmployee> orglist(@RequestParam("organizationId") String orgid) {		return userManagerLogic.getUserList(orgid);	}		@RequestMapping("/user/get")	@ResponseBody	public HrEmployee getDetail(@RequestParam("userId") String userId) {		return userManagerLogic.getDetail(userId);	}		@RequestMapping("/user/create")	@ResponseBody	public HrEmployee save(@RequestBody HrEmployee instance) {		return userManagerLogic.save(instance);	}		@RequestMapping("/user/update")	@ResponseBody	public HrEmployee update(@RequestBody HrEmployee instance) {		return userManagerLogic.update(instance);	}		@RequestMapping("/user/remove")	@ResponseBody	public void remove(@RequestBody HrEmployee instance) {		userManagerLogic.delete(instance);	}}