package com.olivee.hrmanager.web.action.orgnization;import java.io.IOException;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import com.olivee.hrmanager.web.logic.OrgnizationManagerLogic;import com.olivee.web.action.filter.WebAction;import com.olivee.web.servlet.ServletResponseTemplate;@Controller("orgnization.MainAction")public class MainAction implements WebAction{		@Autowired	private OrgnizationManagerLogic orgnizationManagerLogic;		@RequestMapping("/orglist")	public void orglist(HttpServletRequest request, HttpServletResponse response) throws IOException{		ServletResponseTemplate.responseJsonData(response, orgnizationManagerLogic.getOrgListJson(""));	}}