package com.olivee.hrmanager.web.action.orgnization;import java.io.IOException;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.ResponseBody;import com.olivee.hrmanager.web.entity.HrOrgnization;import com.olivee.hrmanager.web.logic.OrgnizationManagerLogic;import com.olivee.web.action.filter.WebAction;@Controller("orgnization.MainAction")public class MainAction implements WebAction{		@Autowired	private OrgnizationManagerLogic orgnizationManagerLogic;		@RequestMapping("/orglist")	@ResponseBody	public List<HrOrgnization> orglist(@RequestParam("parentId") String parentId) {		return orgnizationManagerLogic.getOrgList("");	}}