package com.olivee.hrmanager.web.logic;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.util.StringUtils;import com.olivee.hrmanager.web.dao.HrOrgnizationHome;import com.olivee.hrmanager.web.entity.HrOrgnization;@Servicepublic class OrgnizationManagerLogic {			@Autowired	private HrOrgnizationHome hrOrgnizationHome;		public List<HrOrgnization> getOrgList(String parentid){		return hrOrgnizationHome.findByParentId(parentid);			}	public HrOrgnization save(HrOrgnization organization) {		hrOrgnizationHome.persist(organization);		return organization;	}	public HrOrgnization update(HrOrgnization organization) {		String id = organization.getId();		if(id==null || id.trim().equals("")){			return organization;		}		hrOrgnizationHome.merge(organization);		return organization;	}		public void delete(HrOrgnization organization) {		hrOrgnizationHome.delete(organization);	}}